import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-details-produit',
  templateUrl: './details-produit.component.html',
  styleUrls: ['./details-produit.component.css']
})
export class DetailsProduitComponent implements OnInit{
  // Attributs
  @Input() dataPassed:any;
  card=JSON.parse(localStorage.getItem('produit') || "{}");


  ngOnInit(): void {

  }


}
<div class="container mt-5 mb-5">
  <span class="btn btn-primary mb-2" routerLink="produit"><i class="bi bi-box-arrow-left"></i> Accueil</span>
  <div class="alert alert-primary text-center" role="alert">
    {{card.titre}}
  </div>
  <div class="row">

    <div class="details-image col-7">
      <img src="{{card.image}}" alt="">
    </div>
    <div class="col-5">
      <p class="badge bg-primary"> Prix : {{card.prix}} fcfa</p> <br>
      <div class="description">
        <h4>Description</h4>
        {{card.description}}
      </div>
